(function () {
  'use strict'

  const calc = document.getElementById('calculator')
  const calcScreen = document.getElementById('src')
  calcScreen.value = 0
  let n1 = ['0']
  let n2 = 0
  let op = null
  let lastop = null
  let result = 0

  const add = (n, o) => n + o
  const sub = (n, o) => n - o
  const div = (n, o) => n / o
  const mul = (n, o) => n * o
  const res = n => n

  const cal = (num1, num2, calback) => {
    if (typeof calback === 'function') {
      return calback(num1, num2)
    }
  }

  const cals = {
    '/': div,
    '*': mul,
    '+': add,
    '-': sub,
    '=': res
  }

  const btn = e => {
    if (
      !e.target.matches('input') ||
            e.target.id === 'src' ||
            e.target.id === 'esound'
    ) {
      return false
    }

    if (e.target.dataset.num) {
      (n1[0] === '0' && n1[1] !== '.' && !!(n1.length = 0)) ||
            n1.push(e.target.value)
    }

    op = null

    if (e.target.dataset.fun) {
      op = e.target.value
    }

    if (op === 'âŒ«') {
      n1 = result
        .toString(10)
        .substring(0, 15)
        .replace(/[^0-9]/g, '.')
        .split('')
      result = 0
      lastop = null
      n1.pop()
    }

    if (!n1.length) {
      n1 = ['0']
    }

    if (op === ',' && !n1.includes('.')) {
      n1.push('.')
    }

    result = n1.join('')

    if (
      op === '/' ||
            op === '*' ||
            op === '+' ||
            op === '-' ||
            op === '='
    ) {
      n1.length = 0

      if (n2 && lastop) {
        result = ((parseFloat(n2) % 1 !== 0) && (parseFloat(result) % 1 !== 0)) ? (cal((parseFloat(n2) % 1 !== 0) ? parseFloat(n2) * 10 : parseFloat(n2), (parseFloat(result) % 1 !== 0) ? parseFloat(result) * 10 : parseFloat(result), cals[lastop]) / 10) : cal(parseFloat(n2), parseFloat(result), cals[lastop])
      }

      n2 = result
      lastop = res(op)
    }

    if (!isFinite(result)) {
      calcScreen.value = 'Ooops!'
      return
    }

    if (op === 'C') {
      n1.length = 0
      n2 = 0
      result = 0
      calcScreen.value = '0'
    }

    calcScreen.value = result

    if (document.getElementById('esound').checked) {
      sound()
    }
  }

  calc.addEventListener('click', btn, true)
  const snd = new window.Audio(
    'data:audio/mp3;base64,'
  )

  function sound () {
    snd.play()
    window.navigator.vibrate(30)
  }
})()
